import PropTypes from 'prop-types';
import React from 'react';
import { connect } from 'react-redux';
import { playerAction, tokenAction, setScore } from '../../../actions';
import { Container, Input, Buttons, Body, Image } from './Styles';

class Login extends React.Component {
  constructor() {
    super();
    this.state = {
      email: '',
      name: '',
      token: '',
      questions: '',
    };
    this.handleChange = this.handleChange.bind(this);
    this.handleClick = this.handleClick.bind(this);
  }

  async handleClick() {
    const { history, saveToken, savePlayer } = this.props;
    const curr = 'https://opentdb.com/api_token.php?command=request';
    const response = await fetch(curr);
    const json = await response.json();
    localStorage.setItem(localStorage.length + 1, JSON.stringify(json.token));
    this.setState({
      token: json.token,
    }, () => {
      savePlayer(this.state);
      saveToken(this.state);
    });
    history.push('/playGame');
  }

  handleChange({ target: { value, name } }) {
    this.setState({
      [name]: value,
    });
  }

  render() {
    const regexEmail = /\S+@\S+\.\S+/;
    const { email, name } = this.state;
    const { history } = this.props;
    const validEmail = regexEmail.test(email);
    return (
      <div>
        <Container>
          <Body />
          <label
            htmlFor="email"
          >
            Email:
            <Input
              onChange={ this.handleChange }
              value={ email }
              name="email"
              type="email"
              data-testid="input-gravatar-email"
            />
          </label>
          <label
            htmlFor="name"
          >
            Nome:
            <Input
              onChange={ this.handleChange }
              value={ name }
              name="name"
              type="text"
              data-testid="input-player-name"
            />
          </label>
          <Buttons
            disabled={ (!validEmail || name.length === 0) }
            onClick={ this.handleClick }
            data-testid="btn-play"
            type="button"
            onMouseOver={ () => {
              som.pause();
              som.currentTime = 0;
              som.play();
            } }
          >
            Jogar
          </Buttons>
          <Buttons
            onClick={ () => { history.push('/config'); } }
            data-testid="btn-settings"
            type="button"
          >
            Configurar
          </Buttons>
        </Container>
      </div>
    );
  }
}

Login.propTypes = {
  history: PropTypes.shape({
    push: PropTypes.func,
  }),
  saveToken: PropTypes.func,
}.isRequired;

const mapDispatchToProps = (dispacth) => ({
  savePlayer: (payload) => dispacth(playerAction(payload)),
  saveToken: (payload) => dispacth(tokenAction(payload)),
  setScores: (payload) => dispacth(setScore(payload)),
});

export default connect(null, mapDispatchToProps)(Login);
