import styled from 'styled-components';

export const Container = styled.div`
max-width: 200px;
padding: 70px;
margin-left: auto;
margin-right: auto;
margin-top: 30vh;
border: solid black 2px;
background-color: purple;
`;

export const Input = styled.input`
margin-top: 10px;
margin-bottom: 10px;
`;

export const Buttons = styled.button`
border-radius: 10%;

&:hover {
    background: red;
}

`;
